import java.util.Scanner;public class CustomExceptions {    public static void main(String[] args) {        class VottingAge extends RuntimeException {            VottingAge(String msg) {                super();                System.out.println(msg);            }        }        Scanner sc = new Scanner(System.in);        System.out.print("Enter Your Age : ");        int age =sc.nextInt();        try {            if(age < 18){                throw new VottingAge("Age is Fixed for voting ..");            } else {                System.out.println("You are Eligible..");            }        } catch (VottingAge v) {            System.out.println("Custom Exception : " + v);        }    }}