import java.util.Collections;import java.util.HashMap;import java.util.Map;import java.util.stream.Collectors;public class NTHSalaryMethod {    public static void main(String[] args) {        Map<String ,Integer> map1 = new HashMap<>();        map1.put("Harshal" ,500000);        map1.put("Rishi" ,50000);        map1.put("Ram Charan" ,40000);        Map.Entry<String ,Integer> res =getNTHSalary(2 , map1);        System.out.println(res);    }    public static Map.Entry<String,Integer> getNTHSalary(int num ,Map<String ,Integer> map1) {        return map1.entrySet().stream().sorted(Collections.reverseOrder(Map.Entry.comparingByValue())).collect(Collectors.toList()).get(1);    }}