import java.util.*;import java.util.stream.Collector;import java.util.stream.Collectors;public class NthMostFrequentWord {    public static void main(String[] args) {        String sentence = "I is a Most Beautiful Language and I love Java and I thinking java " +                "is most powerful language ";        int n = 1;        Map.Entry<String, Integer> res = getNTHFrequentSentences(sentence, n);        System.out.println(res.getKey() + " = " + res.getValue() );    }   public static Map.Entry<String,Integer> getNTHFrequentSentences(String sentences , int n) {       Map<String, Integer> wordCount = Arrays.stream(sentences.toLowerCase().split("\\s")).collect(Collectors.toMap(word -> word, word -> 1,Integer::sum));       return wordCount.entrySet().stream().sorted(Collections.reverseOrder(Map.Entry.comparingByValue())).toList().get(n-1);   }}