import java.util.Arrays;import java.util.LinkedHashSet;public class RemoveDuplicate {    public static int[] removeDuplicate(int arr[]) {        LinkedHashSet<Integer> set = new LinkedHashSet<>();        for (int num : arr) {            set.add(num);        }        return set.stream().mapToInt(Integer::intValue).toArray();    }    public static void main(String[] args) {        int arr[] = {1,2,3,3,4,5,6,6,7,5};        System.out.println("Remove Duplicate : "+ Arrays.toString(removeDuplicate(arr)));    }}