import java.util.ArrayList;public class ReverceString {    public static void main(String[] args) {        String str = "I lOVE JAVA";        String[] strArr = str.split(" ");        ArrayList<String> list = new ArrayList<>();        for (int i = strArr.length -1; i >= 0 ; i --) {             list.add(strArr[i]);        }        list.forEach(s -> {            System.out.print(s + " ");        });    }}